INSERT INTO `wp_options` VALUES ('3179', '_site_transient_ai1ec_update_plugins', 'a:1:{s:55:"all-in-one-event-calendar/all-in-one-event-calendar.php";a:11:{s:6:"plugin";s:55:"all-in-one-event-calendar/all-in-one-event-calendar.php";s:11:"new_version";s:6:"2.5.28";s:7:"version";s:6:"2.5.28";s:4:"name";s:4:"Core";s:4:"slug";s:25:"all-in-one-event-calendar";s:3:"url";s:15:"https://time.ly";s:8:"homepage";s:15:"https://time.ly";s:7:"package";s:70:"https://checkout.time.ly/packages/all-in-one-event-calendar_2.5.28.zip";s:13:"download_link";s:70:"https://checkout.time.ly/packages/all-in-one-event-calendar_2.5.28.zip";s:8:"sections";a:2:{s:11:"description";s:390:"The <a href="https://time.ly" target="_blank">All-in-One Event Calendar</a> from\r\n<a href="https://time.ly" target="_blank">Timely</a> is a beautiful way to list your events in\r\nWordPress and easily share them with the rest of the world. Our\r\ncalendar system combines clean visual design with a powerful set of\r\nfeatures to create the most advanced calendar system available for\r\nWordPress.";s:9:"changelog";s:282:"<strong>2.5.28</strong>\r\n<ul>\r\n	<li>Fixed: On the event preview page, the date shown was incorrect.</li>\r\n	<li>Fixed "http_build_query(): Parameter 1 expected to be Array or Object" warning.</li>\r\n	<li>Security fix: Added security check before cleaning TWIG cache files.</li>\r\n</ul>";}s:6:"author";s:65:"<a href="https://time.ly" target="_blank">Timely Network Inc.</a>";}}', 'no'); 
